mapper.insert:

insert into t_app_editions (id, name, package_name, 
  version, app_size, download_times, 
  download_url, lowest_system, application_id, 
  application_name, create_time, is_delete
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{packageName,jdbcType=VARCHAR}, 
  #{version,jdbcType=VARCHAR}, #{appSize,jdbcType=VARCHAR}, #{downloadTimes,jdbcType=INTEGER}, 
  #{downloadUrl,jdbcType=VARCHAR}, #{lowestSystem,jdbcType=VARCHAR}, #{applicationId,jdbcType=INTEGER}, 
  #{applicationName,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=VARCHAR}
  )

mapper.select:
  		select * from t_app_editions where is_delete='n'


			and name like CONCAT('%',#{name},'%')



			and application_id=#{applicationId}
