mapper.insert:

insert into t_column_type (id, name, content, 
  status, create_time, column_type, 
  flag, is_delete, column_id,companyid
  )
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, 
  #{status,jdbcType=VARCHAR}, #{createTime,jdbcType=TIMESTAMP}, #{columnType,jdbcType=VARCHAR}, 
  #{flag,jdbcType=VARCHAR}, #{isDelete,jdbcType=VARCHAR}, #{columnId,jdbcType=INTEGER}, #{companyid,jdbcType=BIGINT}
  )

mapper.select:
  	select * from t_column_type where is_delete='n' 


		and companyid=#{companyid}


		and column_type=#{type}


		and status=#{status}


		and status=#{status}


			and  name like CONCAT('%',#{name},'%') 

  	order by flag desc
