mapper.select:
select

from parameter where 1=1 and isshow=1

	and id=#{id}


	and ptype=#{ptype}


	and pcode=#{pcode}


	and pname=#{pname}

select * from parameter where 1=1

	and id =#{id}


	and ptype =#{ptype}


	and pname =#{pname}


	and (ptype like '%${ptype}%'
	or pname like '%${pname}%')

select * from parameter 
where ptype = 'industry' 
and pname = #{pname}

mapper.insert:
insert into parameter (id,
ptype, pname,
isshow, porder, pcode
)
values (#{id,jdbcType=INTEGER},
#{ptype,jdbcType=VARCHAR},
#{pname,jdbcType=VARCHAR},
#{isshow,jdbcType=INTEGER}, #{porder,jdbcType=INTEGER},
#{pcode,jdbcType=INTEGER}
)
