mapper.select:
select

from t_sys_parameter
where is_delete='n' and id = #{id,jdbcType=BIGINT}

select

from t_sys_parameter where is_delete='n'

	id = #{id,jdbcType=BIGINT}


	and companyid=#{companyid}


	and name=#{name}

select * from t_sys_parameter where is_delete='n'

	and parameter_type=#{parameterType}


	and (
	name like CONCAT('%',#{name},'%')
	or sys_describe like CONCAT('%',#{name},'%')
	)


	and companyid=#{companyid}

order by id desc
