mapper.select:
select

from t_com_dealer
where 1=1

	and sid = #{sid,jdbcType=BIGINT}


	and companyid = #{companyid,jdbcType=BIGINT}


	and operatorid = #{operatorid,jdbcType=BIGINT}


	and name = #{name,jdbcType=VARCHAR}


	and name like CONCAT('%',#{content},'%')

mapper.insert:
insert into t_com_dealer (sid,
parentid, name,
pwd, privilege, createtime
)
values
(#{sid,jdbcType=BIGINT}, #{parentid,jdbcType=VARCHAR},
#{name,jdbcType=VARCHAR},
#{pwd,jdbcType=VARCHAR},
#{privilege,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP}
)
