mapper.select:
select 

from t_company
where 1=1 

	 and sid = #{sid,jdbcType=BIGINT}


	 and companyname = #{companyname,jdbcType=VARCHAR}

mapper.insert:
insert into t_company (sid, companyname, companycode, 
  role, loginpwd, token, 
  boss, phone, createtime, 
  isopen, paytime, validtime
  )
values (#{sid,jdbcType=BIGINT}, #{companyname,jdbcType=VARCHAR}, #{companycode,jdbcType=VARCHAR}, 
  #{role,jdbcType=INTEGER}, #{loginpwd,jdbcType=VARCHAR}, #{token,jdbcType=VARCHAR}, 
  #{boss,jdbcType=VARCHAR}, #{phone,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}, 
  #{isopen,jdbcType=INTEGER}, #{paytime,jdbcType=TIMESTAMP}, #{validtime,jdbcType=TIMESTAMP}
  )
