mapper.insert:
insert into t_image_text (id, name, status,
line_status, show_type, content,
create_time, is_delete, templet,
image_attribute, last_image_text, lable_attribute,companyid,operatorid,dealerid
)
values (#{id,jdbcType=INTEGER}, #{name,jdbcType=VARCHAR},
#{status,jdbcType=VARCHAR},
#{lineStatus,jdbcType=VARCHAR}, #{showType,jdbcType=INTEGER}, #{content,jdbcType=VARCHAR},
#{createTime,jdbcType=TIMESTAMP}, #{isDelete,jdbcType=VARCHAR},
#{templet,jdbcType=VARCHAR},
#{imageAttribute,jdbcType=VARCHAR}, #{lastImageText,jdbcType=VARCHAR},
#{lableAttribute,jdbcType=VARCHAR}
,#{companyid,jdbcType=BIGINT}
,#{operatorid,jdbcType=BIGINT}
,#{dealerid,jdbcType=BIGINT}
)

mapper.select:
select * from t_image_text where is_delete='n'

	and companyid=#{companyid}


	and id=#{id}


	and status=#{status}


	and line_status=#{lineStatus}


	and name like CONCAT('%',#{name},'%')


	and name like CONCAT('%',#{name},'%')


	and show_type=#{showType}

order by create_time desc
